import React from 'react';

class InputForm extends React.Component {
     constructor(props) {
         super(props);

         this.state = {
          value: ''   
         }
         this.handleSubmit = this.handleSubmit.bind(this);

         this.handleChange = this.handleChange.bind(this);
     }

     handleSubmit(event){
         event.preventDefault();
        console.log(this.state.value);
        return() => {
            this.props.uppdateSortBy(this.state.value) 
        }
     }

     handleChange(event){
        this.setState({
            value: event.target.value
        })
     }

     handleClick = () => {
        return() => {
            this.props.uppdateSortBy(this.state.value) 
        }
     }


render () {
    // const { sort_by, uppdateSortBy } = this.props;
    return (
        <form onSubmit={this.handleSubmit} className="input-group">
            <input style={{borderRadius: "5px"}} value={this.state.value} onChange={this.handleChange} type="text" className="border thisInp" placeholder="Search page"/>
            <button type="submit" onClick={this.handleClick(this.state.value)} className="btn btn-warning btn-sm">SearcH</button>
        </form>
    )
  }
}

export default InputForm;
