import React from 'react'
// import ClassNames from 'classnames'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faCaretSquareRight, faCaretSquareLeft} from '@fortawesome/free-solid-svg-icons'



const MovieTabs = (props) => {
    const { sort_by, uppdateSortBy, data} = props;

    const handleClickM = value => {
        return () => {
            if(value >= 2){
                uppdateSortBy(value - 1)
            }else if(value === 1){
                (alert("Non page"))
            }
        }
    };

    const handleClickN = value => {
        return () => {
                uppdateSortBy(value + 1)
        }
    };

    const handleChainge = (event) => {
        this.setState({
            value:event.target.value
        })
    };

    const handleSubmit = event => {
        console.log('Ім\'я, що було надіслано: ' + this.state.value);
        event.preventDefault();
      }


    return (
        <div className="row ml-4 ml-sm-0">
            <ul className="px-0 tabs nav nav-pills mt-2 flex-nowrap col-12 col-md-6">
                <li className="nav-item">
                    <div style={{cursor: 'pointer', borderRadius: '5px'}} className="navigation nav-link p-1"
                    onClick={handleClickM(sort_by)}
                    >
                        <FontAwesomeIcon style={{fontSize: "50px"}} icon={faCaretSquareLeft}  />
                    </div>
                </li>
                <li className="nav-item mx-3 flex-nowrap">
                    <div style={{cursor: 'pointer', border: '1px black solid', borderRadius: '5px'}} className="px-1">
                        <div>
                            Total Pages: {data.total_pages}
                        </div>
                        <div className="nav-link mt-2 mx-3">
                            This page: {sort_by}
                        </div>
                    </div>
                </li>
                <li className="nav-item">
                    <div style={{cursor: 'pointer', borderRadius: '5px'}} className="navigation nav-link p-1 "
                    onClick={handleClickN(sort_by)}
                    >
                        <FontAwesomeIcon style={{fontSize: "50px"}} icon={faCaretSquareRight}  />
                    </div>
                </li>
            </ul>
            <form  className="mt-2 px-0 input-group col-12 col-md-6 align-content-center">
                <input style={{borderRadius: "5px"}} type="text" ckassName="border" placeholder="Search page" value={sort_by} onChange={handleChainge()}/>
                <button type="submit" className="btn btn-warning btn-sm">SearcH</button>
            </form>
        </div>    
    )
}

export default MovieTabs;
