import React from "react";
import {moviesData} from '../moviesData'
import WillWatch from './WillWatch'

class App extends React.Component {
  render() {
    return (
      <div className="container">
       <div className="row">  
        <div className="col-9">
          <MovieItem />
        </div>
        <div className="col-3">
          <WillWatch />
        </div>
       </div> 
      </div> 
              )
            }
          };

class Button extends React.Component{
  constructor() {
    super();

    this.state={
      willWatch: true,
      movies: moviesData
    }
  }
  render() {
    return (
      <div>
      {this.state.movies.map(movie => {
      <button style={{background: this.state.willWatch ? "green" : "red"}} type="button" className="btn btn-secondary col-sm-12 col-lg-6" 
                  onClick ={() => {
                    console.log('Clikkk', movie.title );
                    this.setState({
                      willWatch: !this.state.willWatch
                    })

                  }}>
                  Will Watch
                     </button>
      })}
      </div>
    )
  }
};          

class MovieItem extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      movies: moviesData,
      willWatch: false
    }
  }

  render(){ 
    const btn = <Button />
    return (
    <div className="row">
        {this.state.movies.map(movie => {
            return (
              <div key={movie.title} className="col-4 mt-4">
              <div className="card">
            <div>
              <img className="card-img-top"
              src={`https://image.tmdb.org/t/p/w500${movie.backdrop_path ||
              movie.poster_path}`} alt=""/>
              <div className="card-body">
               <h6 className="card-title">{movie.title}</h6>
                <div className="d-flex justify-content-between align-items-center row">
                  <p className="mb-0 col-sm-12 col-lg-6">Rating: {movie.vote_average}</p>
                  {btn}
                </div>
              </div>
             </div> 
             </div>
             </div>
               )
        })}
    </div>
    )
  }
}     

export default App;
