import React from 'react';
import {moviesData} from '../moviesData'

class Button extends React.Component{
    constructor(props) {
        super(props);

        this.state = {
          movies: moviesData,
          willWatch: false
        }
      }
    render(){
        return(
            <div className="row">

                  <button style={{background: this.state.willWatch ? "green" : "red"}} type="button" className="btn btn-secondary col-sm-10 ml-sm-4 col-lg-10 m" 
                  onClick ={() => {
                    console.log('Clikkk', moviesData.title);
                    this.setState({
                      willWatch: !this.state.willWatch
                    })

                  }}>
                  Will Watch
                     </button>

    </div>
        )
    }
}

export default Button
