import React from "react";
import {moviesData} from '../moviesData'
import WillWatch from './WillWatch'

class App extends React.Component {
  render() {
    return (
      <div className="container">
       <div className="row">  
        <div className="col-9">
          <MovieItem />
        </div>
        <div className="col-3">
          <WillWatch />
        </div>
       </div> 
      </div> 
              )
            }
          }

class MovieItem extends React.Component {
  constructor() {
    super();

    this.state = {
      movies: moviesData,
      WillWatch: false
    }
  }
  render(){
    const {movies} = this.props
    return (
    <div className="">
        {console.log("I can see this state => ", this.state)}
        {/* <p> {!this.state.willWatch ? "NOT will" : "will"} </p> */}
        {movies.map(movie => {
          // return <p> {movie.title} </p>;
          return (
            <div className="offset-1 col-10 col-sm-6 mt-4" key={movie.id}>
              <div className="card">
                <img
                  className="card-img-top"
                  src={`https://image.tmdb.org/t/p/w500${movie.backdrop_path ||
                    movie.poster_path}`}
                  alt=""
                />
                <div className="card-body">
                  {/* {console.log("I CAN NOT see this state => ",this.state)} */}
                  {/* <p> {this.state.willWatch ? "NOT will" : "will"} </p> */}
                  <h6 className="card-title">{movie.title}</h6>
                  <div className="d-flex justify-content-between align-items-center">
                    <p className="mb-0">Rating: {movie.vote_average}</p>
                    <button
                      type="button"
                      className="btn btn-warning"
                      onClick={() => {
                        console.log("Clickkk", this.state.willWatch);
                        this.state.willWatch
                          ? this.setState({
                              willWatch: false
                            })
                          : this.setState({
                              willWatch: true
                            });
                      }}
                    >
                      Will Watch
                    </button>
                </div>
              </div>
             </div> 
             </div>
               )
        })}
    </div>
    )
  }
}     

export default App;
