import React from 'react';
import {moviesData} from '../moviesData';

class Button extends React.Component{
    constructor(props) {
        super(props);

        this.state = {
          movies: moviesData,
          willWatch: true
        }
      }
    render(){
        return(
            <div className="row">
                  <button style={{background: this.state.willWatch ? "green" : "red"}} type="button" className="btn btn-secondary col-10 ml-2 px-md-4 col-sm-10 col-md-10 ml-md-3 col-lg-12 ml-lg-4 ml-lg-n4" 
                  onClick ={() => {

                    this.setState({
                      willWatch: !this.state.willWatch
                    })
                    }}>
                  {this.state.willWatch ? 'Will Watch' : 'not Will Watch'}
                     </button>
    </div>
        )
    }
}

export default Button
