import React from "react";
import {moviesData} from '../moviesData'
import WillWatch from './WillWatch'

class App extends React.Component {
  render() {
    return (
      <div className="container">
       <div className="row">  
        <div className="col-9">
          <MovieItem />
        </div>
        <div className="col-3">
          <WillWatch />
        </div>
       </div> 
      </div> 
              )
            }
          };


class Button extends React.Conponent{
  r
}


class MovieItem extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      movies: moviesData,
      willWatch: true,
      color: true
    }
  }

  render(){ 
    return (
    <div className="row">
        {this.state.movies.map(movie => {
            return (
              <div key={movie.id} className="col-4 mt-4">
              <div className="card">
            <div>
              <img className="card-img-top"
              src={`https://image.tmdb.org/t/p/w500${movie.backdrop_path ||
              movie.poster_path}`} alt=""/>
              <div className="card-body">
               <h6 className="card-title">{movie.title}</h6>
                <div className="d-flex justify-content-between align-items-center row">
                  <p className="mb-0 col-sm-12 col-lg-6">Rating: {movie.vote_average}</p>
                  <button type="button" className="btn btn-secondary col-10 ml-2 px-md-4 col-sm-10 col-md-10 ml-md-3 col-lg-10 mt-2 ml-lg-4" 
                  onClick ={() => {
                    console.log('Clikkk', movie.title, movie.id);
                    this.setState({
                      willWatch: !this.state.willWatch
                    })
                }}>
                  {this.state.willWatch ? 'Will Watch' : 'not Will Watch'}
                     </button>
                </div>
              </div>
             </div> 
             </div>

             </div>
               )
        })}
    </div>
    )
  }
}     

export default App;
