import React from "react";
import {moviesData} from '../moviesData'
import WillWatch from './WillWatch'

class App extends React.Component {
  render() {
    return (
      <div className="container">
       <div className="row">  
        <div className="col-9">
          <MovieItem />
        </div>
        <div className="col-3">
          <WillWatch />
        </div>
       </div> 
      </div> 
              )
            }
          }

class MovieItem extends React.Component {
  constructor() {
    super();

    this.state = {
      movies: moviesData,
      willWatch: false
    }
  }

  render(){ 
    return (
    <div className="row">
        {this.state.movies.map(movie => {
            return (
              <div key={movie.id} className="col-4 mt-4">
              <div className="card">
            <div>
              <img className="card-img-top"
              src={`https://image.tmdb.org/t/p/w500${movie.backdrop_path ||
              movie.poster_path}`} alt=""/>
              <div className="card-body">
               <h6 className="card-title">{movie.title}</h6>
                <div className="d-flex justify-content-between align-items-center row">
                  <p className="mb-0 col-sm-12 col-lg-6">Rating: {movie.vote_average}</p>
                  <button type="button" className="btn btn-secondary col-sm-12 col-lg-6" onClick={this.wiil}>
                  {this.state.willWatch ? 'Will Watch' : 'NOT Will Watch'}
                     </button>
                </div>
              </div>
             </div> 
             </div>
             </div>
               )
        })}
    </div>
    )
    wiil = () =>{
    console.log('Clikkk', movie.title );
                    this.state.willWatch
                          ? this.setState({
                              willWatch: false
                            })
                          : this.setState({
                              willWatch: true
                            });
  }
  }
}     

export default App;
